"use strict";
$(document).ready(function () {
    $("#datatable").DataTable({
        pageLength: 10,
        lengthMenu: [10, 20, 50, 100, 200, 500],
    });
    $("#datatableCreate").DataTable({
        pageLength: 1000,
        lengthMenu: [[1000], ["all"]],
    });
}),
    $(document).ready(function () {
        $("#checkAll").change(function () {
            this.checked
                ? $(".checkbox").each(function () {
                      (this.checked = !0),
                          $(this)
                              .parent()
                              .parent()
                              .parent()
                              .parent()
                              .addClass("selected");
                  })
                : $(".checkbox").each(function () {
                      (this.checked = !1),
                          $(this)
                              .parent()
                              .parent()
                              .parent()
                              .parent()
                              .removeClass("selected");
                  });
        }),
            $(".checkbox").click(function () {
                var e;
                $(this).is(":checked")
                    ? ((e = 0),
                      $(".checkbox").each(function () {
                          this.checked || (e = 1);
                      }),
                      0 == e && $("#checkAll").prop("checked", !0))
                    : $("#checkAll").prop("checked", !1);
            }),
            $(".items-wrapper").on("click", "input:checkbox", function () {
                $(this)
                    .closest(".single-items")
                    .toggleClass("selected", this.checked);
            }),
            $(".items-wrapper input:checkbox:checked")
                .closest(".single-items")
                .addClass("selected");
    });
